mapper.select:
SELECT * FROM patient WHERE
pId = #{searchTerm}
(name LIKE CONCAT('%', #{searchTerm}, '%') OR sex LIKE CONCAT('%', #{searchTerm}, '%') OR phone LIKE CONCAT('%', #{searchTerm}, '%') OR diagnosticTime LIKE CONCAT('%', #{searchTerm}, '%') OR allergy LIKE CONCAT('%', #{searchTerm}, '%') OR doctorAdvice LIKE CONCAT('%', #{searchTerm}, '%') OR dName LIKE CONCAT('%', #{searchTerm}, '%') OR lastDiaTime LIKE CONCAT('%', #{searchTerm}, '%'))
